
function zhishu_js(num) {
    if (num == 1) {
        return false;
    }
    if (num == 2) {
        return true;
    }
    for (var i = 2; i <= Math.sqrt(num); i++) {
        if (num % i == 0) {
            return false;
        }
    }
    return true;
}

var count = 0;
var a =new Date().getTime();
 
for (j = 1; j < 4000000; j++) {
    if(zhishu_js(j)){
        count++;
    }
}

var b=new Date().getTime();
console.log( 'time:'+ (b-a));
console.log(count);