var http = require('http');  
    http.createServer(function(req, res){  
        var html = '<html>'  
        +'<head>'  
        +'<title>nodejs</title>'  
        +'<style type="text/css">'  
        +'body{color:red;}'  
        +'</style>'  
        +'</head>'  
        +'<body>'  
        +   'hello world! 1234'  
        +'</body>'  
        +'</html>';  
        res.writeHead(200,{'Content-Type' : 'text/html'});  
        res.write(html);  
        res.end();  
    }).listen(1337);  