var net = require('net');

var HOST = '127.0.0.1';
var PORT = 1337;
var consolelog = '';

var client = new net.Socket();
client.connect(PORT , HOST, function () {
	console.log('CONNECTED TO: ' + HOST + ':' + PORT);
	consolelog += 'CONNECTED TO: ' + HOST + ':' + PORT + '\n';
	exports.output = consolelog;

	// 建立连接后立即向服务器发送数据，服务器将收到这些数据
	client.write('I is LongHongXuan!');
	sleep2(1000);

});

// 为客户端添加“data”事件处理函数
// data是服务器发回的数据
client.on('data', function (data) {
	console.log('DATA: ' + data);
	consolelog += 'DATA: ' + data+'\n';
	exports.output = consolelog;
	// 完全关闭连接
	client.destroy();
	// client.end();
});

// 为客户端添加“close”事件处理函数
client.on('close', function () {
	console.log('Connection closed');
	consolelog += 'Connection closed. \n';
	exports.output = consolelog;
});

function sleep2(numberMillis) {
	var now = new Date();
	var exitTime = now.getTime() + numberMillis;
	while (true) {
		now = new Date();
		if (now.getTime() > exitTime)
			return;
	}
}